import React, { useEffect, useState } from 'react'
import ReactDOM from 'react-dom'
import axios from 'axios'
import MoviesList from './components/MoviesList/MoviesList'
import MovieForm from './components/MovieForm/MovieForm'
import './main.css'
import Header from './components/HeaderFooter/Header';
import Footer from './components/HeaderFooter/Footer';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom'

// A functional component which returns JSX
function App() {
  const [movies, setMovies] = useState([])

  // Similar to componentDidMount
  useEffect(() => {
    // Similar to fetch but optimized for React
    axios.get('http://localhost:4000/api/movies')
      .then(response => setMovies(response.data))
  }, [])

  // Add function to delete movies,which will be passed down as a prop
  const onDelete = (movieId) => {
    axios.delete(`http://localhost:4000/api/movie/${movieId}`)
      // Re-render the page minus the deleted movie by adjusting state rather than making another server call
      .then(({ data }) => setMovies(movies.filter(movie => movie._id != data._id)))
  }

  // Add functionality to add new movies, which will be passed down as a prop
  const onAdd = (movie) => {
    axios.post('http://localhost:4000/api/movie/new', movie)
  }

  //Add functionality to edit existing movies, which will be passed down as a prop
  const onEdit = (movie) => {
    axios.put(`http://localhost:4000/api/movie/${movie._id}`, movie)
  }

  // Create props object for component
  const movieProps = { movies, onDelete }
  const editProps = { movies, onEdit }

  return (
    <Router>
      <Header />
      <Routes>
        <Route exact path="/" element={<MoviesList {...movieProps} />}></Route>
        <Route exact path="/add" element={<MovieForm addMovie={onAdd} />}></Route>
        <Route exact path="/edit/:movieId" element={<MovieForm {...editProps} />}></Route>
      </Routes>
      <Footer />
    </Router>
  )
}

// Render the App component into the DOM in the referenced element
ReactDOM.render(<App />, document.getElementById('root'))
