import React from 'react';
  import "./movie-block.css";
  import { Link } from 'react-router-dom'

  // MovieBlock functional component accepting the deconstructed prop of "movie"
  function MovieBlock({ movie, deleteMovie }) {
    return (
      <div className="movie-block">
        <h3>{ movie.name }</h3>
        <p>{ movie.year } {'\u00b7'} { movie.rating } {'\u00b7'} { movie.length }</p>
        <p><em>{ movie.description }</em></p>
        <p><b>Genre:</b> { movie.genre.join(', ') }</p>
        <p><b>Stars:</b> { movie.stars.join(', ') }</p>
        <p><b>Director:</b> { movie.director }</p>
        <Link to={`/edit/${movie._id}`}>
          <button type="button" className="btn btn-edit">Edit</button>
        </Link>
        <button type="button" className="btn btn-delete" onClick={() => deleteMovie(movie._id)}>Delete</button>
      </div>
    )
  };

  export default MovieBlock;
