import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom'
import "./movie-form.css";

  function MovieForm({ addMovie, editMovie, movies }) {
    const [inputs, setInputs] = useState({});
    let urlParams = useParams()

    const handleChange = (event) => {
      const name = event.target.name
      const value = event.target.value
      setInputs(values => ({ ...values, [name]: value }))
    };

    const handleSubmit = (event) => {
      event.preventDefault()
      if (movies) {
        editMovie(inputs)
      } else {
        addMovie(inputs)
      }
      window.location = "/"
    }

    useEffect(() => {
      let defaults = movies 
        ? movies.filter(movie => movie._id === urlParams.movieId)[0] 
        : {}
      setInputs({...defaults})
    }, [movies])

    return (
      <div className='movies-form'>
        <form id="create-movies" onSubmit={handleSubmit}>
          <label>Movie Title:</label>
          <input 
            type="text"
            name="name"
            value={inputs.name || ""}
            onChange={handleChange}
          />
          <label>Description:</label>
          <textarea
            name="description"
            cols="30"
            rows="10"
            value={inputs.description || ""}
            onChange={handleChange}>
          </textarea>
          <label>Rating:</label>
          <input
            type="text"
            name="rating"
            value={inputs.rating || ""}
            onChange={handleChange}
          />
          <label>Length:</label>
          <input
            type="text"
            name="length"
            value={inputs.length || ""}
            onChange={handleChange}
          />
          <label>Year Released:</label>
          <input
            type="number"
            name="year"
            value={inputs.year || ""}
            onChange={handleChange}
          />
          <label>Genres (separated by commas and spaces):</label>
          <input
            type="text"
            name="genre"
            value={inputs.genre || ""}
            onChange={handleChange}
          />
          <label>Stars (separated by commas and spaces):</label>
          <input
            type="text"
            name="stars"
            value={inputs.stars || ""}
            onChange={handleChange}
          />
          <label>Director:</label>
          <input
            type="text"
            name="director"
            value={inputs.director || ""}
            onChange={handleChange}
          />
          <button type="submit" id="movie-submit">{movies ? 'Edit' : 'Add'} Movie</button>
          <Link to="/">
            <button type="button" id="movie-cancel">Cancel</button>
          </Link>
        </form>
      </div>
    )
  };

  export default MovieForm;
